    return (
        <div>
            <div class = "form-row">
                <div class = "form-group col mb-3">
                    <input class="form-check-input" type="checkbox" value="" id="lenType" />
                    <label class="form-check-label" for="lenType">
                        {id == "len" && <LengthCriteria text={description}/>}
                        {id != "len" && description}
                    </label>

                    {/* <Form.Check type="checkbox" label={description}></Form.Check> */}

                </div>
            </div>

            {/* <input type="checkbox" id={description}></input>
            <label for={description}>{description}</label>
            <input type="text" id={description} placeholder="Specific characters wanted"></input> */}
        </div>
    )
}

export default Criteria
